CommandUIHandler.CommandAction:
javascript:
                          var url = '', listUrl = '';
                          if(_spPageContextInfo.webServerRelativeUrl.substr(_spPageContextInfo.webServerRelativeUrl.length - 1) != '/'){
                             url = _spPageContextInfo.webServerRelativeUrl + '/'; //if subsite
                             listUrl = _spPageContextInfo.webServerRelativeUrl + '/';
                          } 
                          else{
                             url = _spPageContextInfo.webServerRelativeUrl;
                          }
                          window.open(url + '_layouts/Onlyoffice/editorPage.aspx?SPListItemId={SelectedItemId}&SPListId={SelectedListId}&SPSource={Source}&SPListURLDir='+escape(listUrl)+'{ListUrlDir}&action=download');

CommandUIHandler.EnabledScript:
javascript:
                              function enableONLYOFFICEButton() {
                                  var items = SP.ListOperation.Selection.getSelectedItems();
                                  //var ci = CountDictionary(items);
                                  var ci = items.length;
                                  if (ci == 1) {
                                      if(items[0].fsObjType == 0){ //0 - file; 1 - folder
                                          return true;
                                      }
                                  }
                              }
                              var clientContext;
                              SP.SOD.executeFunc('sp.js', 'SP.ClientContext', sharePointReady);

                              // Create an instance of the current context.
                              function sharePointReady() {
                                  clientContext = SP.ClientContext.get_current();
                                  clientContext.executeQueryAsync(onRequestSucceeded, onRequestFailed);
                              }
                              function onRequestSucceeded() {
                                  var serverVersion = clientContext.get_serverVersion();
                                  if(serverVersion){
                                      var ribbonButton = document.getElementById('Ribbon.Documents.Manage.RibbonActionOnlyoffice-Large');
                                      serverVersion = serverVersion.split('.')[0];

                                      if(serverVersion == '14') {                                    
                                          ribbonButton.getElementsByTagName('img')[0].src = ribbonButton.getElementsByTagName('img')[0].src.replace('/15','');
                                      }
                                  }
                              }
                              function onRequestFailed(sender, args) {
                                  console.log('Error: ' + args.get_message());
                              }
                              enableONLYOFFICEButton();
